import React,{Component, Fragments} from 'react';
import 'antd/dist/antd.css';
import { Input } from 'antd';
import { Button } from 'antd';
import { List, Typography } from 'antd';
import store from './store';
import {getInputChangeAction} from './store/actionCreator'
import {CHANGE_INPUT_VALUE, ADD_TODO_ITEM, DELETE_TODO_ITEM} from './store/actionTypes';


class TodoList extends Component{
constructor(props){
    super(props);
    this.state = store.getState();

    this.handleInputChange = this.handleInputChange.bind(this);
    this.handleStoreChange = this.handleStoreChange.bind(this);
    this.handleBtnClick = this.handleBtnClick.bind(this);
    store.subscribe(this.handleStoreChange);


}
    render(){
        return (
        <div style = {{marginTop: '10px', marginLeft: '10px'}}>    
        <Input value = {this.state.inputValue}
            placeholder = 'todo info' 
            style = {{width : '300px', marginRight: '10px'}}
            onChange = {this.handleInputChange}/>
        <Button onClick = {this.handleBtnClick} type="primary">Primary</Button>
        <List
      size="small"
      style = {{marginTop: '10px', width: '300px'}}
      bordered
      dataSource={this.state.list}
      renderItem={(item,index) => (<List.Item onClick= {this.handleitemDelete.bind(this, index)}>{item}</List.Item>)}
    />
        </div>

            )

    }

    handleInputChange(e){
        const action = {
            type: CHANGE_INPUT_VALUE,
            value: e.target.value


        }
        store.dispatch(action)

    }
    handleStoreChange(){
        this.setState(store.getState());
    }
    handleBtnClick(){
        const action = getInputChangeAction(e.tarhet.value)
        store.dispatch(action);
    }
    handleitemDelete(index){
        const action = {
            type: DELETE_TODO_ITEM,
            index

        }
        store.dispatch(action);
    }
}



export default TodoList;
